import React, { Component } from 'react';
import withRouter from '../../withRouter';
import '../../App.css';

class ViewAllCars extends Component {
    constructor(props) {
        super(props);

        this.state = {
            showProfileWindow: false,
            selectedCarType: 'Compact',
            showPaymentModal: false,
            bookedVehicle: null,
            carData: {
                'Compact': [
                    {
                        "Available period": "2023-04-26 to 2023-05-03",
                        "Car Type": "Compact",
                        "Model": "Toyota Corolla",
                        "Vehicle Id": 1001,
                        "Year": 2020
                    },
                    {
                        "Available period": "2023-05-04 to 2023-05-10",
                        "Car Type": "Compact",
                        "Model": "Honda Civic",
                        "Vehicle Id": 1003,
                        "Year": 2021
                    },
                    {
                        "Available period": "2023-05-11 to 2023-05-18",
                        "Car Type": "Compact",
                        "Model": "Nissan Sentra",
                        "Vehicle Id": 1006,
                        "Year": 2019
                    },
                    {
                        "Available period": "2023-05-19 to 2023-05-25",
                        "Car Type": "Compact",
                        "Model": "Ford Focus",
                        "Vehicle Id": 1008,
                        "Year": 2022
                    },
                    {
                        "Available period": "2023-05-26 to 2023-06-01",
                        "Car Type": "Compact",
                        "Model": "Chevrolet Cruze",
                        "Vehicle Id": 1012,
                        "Year": 2020
                    }
                ],
                'Medium': [
                    {
                        "Available period": "2023-05-04 to 2023-05-30",
                        "Car Type": "Medium",
                        "Model": "Honda Accord",
                        "Vehicle Id": 1005,
                        "Year": 2022
                    },
                    {
                        "Available period": "2023-06-01 to 2023-06-15",
                        "Car Type": "Medium",
                        "Model": "Toyota Camry",
                        "Vehicle Id": 1007,
                        "Year": 2021
                    },
                    {
                        "Available period": "2023-06-16 to 2023-06-30",
                        "Car Type": "Medium",
                        "Model": "Nissan Altima",
                        "Vehicle Id": 1010,
                        "Year": 2020
                    },
                    {
                        "Available period": "2023-07-01 to 2023-07-15",
                        "Car Type": "Medium",
                        "Model": "Ford Fusion",
                        "Vehicle Id": 1013,
                        "Year": 2022
                    },
                    {
                        "Available period": "2023-07-16 to 2023-07-31",
                        "Car Type": "Medium",
                        "Model": "Chevrolet Malibu",
                        "Vehicle Id": 1016,
                        "Year": 2021
                    }
                ],
                'SUV': [
                    {
                        "Available period": "2023-06-01 to 2023-06-15",
                        "Car Type": "SUV",
                        "Model": "Chevrolet Tahoe",
                        "Vehicle Id": 1011,
                        "Year": 2022
                    },
                    {
                        "Available period": "2023-06-10 to 2023-06-30",
                        "Car Type": "SUV",
                        "Model": "Jeep Wrangler",
                        "Vehicle Id": 1012,
                        "Year": 2020
                    },
                    {
                        "Available period": "2023-07-05 to 2023-07-20",
                        "Car Type": "SUV",
                        "Model": "Nissan Armada",
                        "Vehicle Id": 1013,
                        "Year": 2023
                    }

                ],
            },
        };

        // Binding functions to the class instance
        this.toggleProfileWindow = this.toggleProfileWindow.bind(this);
        this.handleLogout = this.handleLogout.bind(this);
        this.handleMakePayment = this.handleMakePayment.bind(this);
    }

    toggleProfileWindow() {
        this.setState((prevState) => ({
            showProfileWindow: !prevState.showProfileWindow,
        }));
    }

    handleLogout = () => {
        const { navigate } = this.props;
        navigate("/");
    };

    handleCarTypeChange = (carType) => {
        this.setState({ selectedCarType: carType });
    };

    handleBookCar = (vehicle) => {
        this.setState({
            showPaymentModal: true,
            bookedVehicle: vehicle
        });
    };

    handleMakePayment = () => {
        const { navigate } = this.props;
        const bookedVehicle = this.state.bookedVehicle;
        this.setState({
            showPaymentModal: false
        });
        alert("Payment successful! Your booking is confirmed.");
        navigate("/customer");
    };

    handleCancelPayment = () => {
        this.setState({
            showPaymentModal: false,
        });
    };

    render() {
        const { selectedCarType, carData } = this.state;
        const selectedCarList = carData[selectedCarType] || [];

        return (
            <div>
                <div className="header">
                    <div className="profile" onClick={this.toggleProfileWindow}>
                        {/* Add profile icon or user details here */}
                        <span className="profile-icon">👤</span>
                    </div>
                    <button className="logout-button" onClick={this.handleLogout}>
                        Logout
                    </button>
                </div>
                <div className="owner-details">
                    <h2>Car Details - {selectedCarType} Cars</h2>
                    <div className="car-type-buttons">
                        <button onClick={() => this.handleCarTypeChange('Compact')}>Compact Cars</button>
                        <button onClick={() => this.handleCarTypeChange('Medium')}>Medium Cars</button>
                        <button onClick={() => this.handleCarTypeChange('SUV')}>SUV Cars</button>
                    </div>
                    {selectedCarList.map((carData, index) => (
                        <div key={index} className="owner-card">
                            <div className="details-row">
                                <p>
                                    <strong>Vehicle ID:</strong> {carData['Vehicle Id']}
                                </p>
                                <p>
                                    <strong>Available Period:</strong> {carData['Available period']}
                                </p>
                                <p>
                                    <strong>Car Type:</strong> {carData['Car Type']}
                                </p>
                                <p>
                                    <strong>Model:</strong> {carData['Model']}
                                </p>
                                <p>
                                    <strong>Year:</strong> {carData['Year']}
                                </p>
                                <div className="button-container">
                                    <button className="book-button" onClick={() => this.handleBookCar(carData)}>
                                        Book
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                    {this.state.showProfileWindow && (
                        <div className="profile-window">
                            {/* Display user profile information */}
                            <p>Full Name: John Doe</p>
                            <p>Email: john.doe@example.com</p>
                            <p>Phone Number: 123-456-7890</p>
                        </div>
                    )}
                    {this.state.showPaymentModal && (
                        <div>
                            <div className="overlay" ></div>
                            <div className="payment-modal">
                                <div className="payment-modal-content">
                                    <h2>Payment</h2>
                                    <p>Advance Amount to be paid: $500.00</p>
                                    <button className="payment-button" onClick={this.handleMakePayment}>Make Payment</button>
                                    <button className="payment-button" onClick={this.handleCancelPayment}>Cancel</button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        );
    }
}

export default withRouter(ViewAllCars);
